$(function(){$(".numbers-row").append('<div class="inc button">+</div><div class="dec button">-</div>'),$(".button").on("click",function(){var n=$(this),t=n.parent().find("input").val();if("+"==n.text())i=parseFloat(t)+1;else if(t>0)var i=parseFloat(t)-1;else i=0;n.parent().find("input").val(i)})});